class Random{static random(){return Math.random()}static randint(t,a){return Math.floor(t+Math.random()*(a-t))}static choice(t){return t[~~(Random.random()*t.length)]}static bool(){return Random.random()>.5}static sign(){return Random.choice([-1,1])}static percent(t){return Random.random()<t/100}}module.exports=Random;
